import React, { useState } from "react";
import {
  Text,
  View,
  Image,
  TouchableOpacity,
  TextInput,
  ScrollView,
  Alert,
} from "react-native";
import { Picker } from '@react-native-picker/picker';
import estilos from "./estilos";
import { criaFinancas } from "../../service/reqs/usuarios";
import DatePicker, { getFormatedDate } from 'react-native-modern-datepicker';
import moment from "moment";

export default function Principal() {
  const [usuario, setUsuario] = useState({ valor: null, local: "", nome: "Renan", data: moment().format("DD/MM/YYYY") });

  async function adicionar() {
    try {
      criaFinancas(usuario).then((response) => {
        if (response) {
          navigation.navigate("Repositorios")
        }
      })
    } catch {
      Alert.alert("Não foi possível cadastrar")
    }
  }

  return (
    <ScrollView>
      <View style={estilos.container}>
        {usuario?.login && (
          <>
            <View style={estilos.fundo} />
            <View style={estilos.imagemArea}>
              <Image
                source={{
                  uri: usuario.avatar_url,
                }}
                style={estilos.imagem}
              />
            </View>
            <Text style={estilos.textoNome}>{usuario.login}</Text>
            <Text style={estilos.textoEmail}>{usuario.email}</Text>
            <View style={estilos.seguidoresArea}>
              <View style={estilos.seguidores}>
                <Text style={estilos.seguidoresNumero}>
                  {usuario.following}
                </Text>
                <Text style={estilos.seguidoresTexto}>Seguidores</Text>
              </View>
              <View style={estilos.seguidores}>
                <Text style={estilos.seguidoresNumero}>
                  {usuario.followers}
                </Text>
                <Text style={estilos.seguidoresTexto}>Seguindo</Text>
              </View>
            </View>
            <TouchableOpacity
              onPress={() =>
                navigation.navigate("Repositorios")
              }
            >
              <Text style={estilos.repositorios}>Ver os repositórios</Text>
            </TouchableOpacity>
          </>
        )}
        <TextInput
          placeholder="Valor"
          autoCapitalize="none"
          value={usuario.valor}
          onChange={(e) => setUsuario((old) => ({ ...old, valor: e.target.value }))}
          style={estilos.entrada}
        />
        <TextInput
          placeholder="Local"
          autoCapitalize="none"
          value={usuario.local}
          onChange={(e) => setUsuario((old) => ({ ...old, local: e.target.value }))}
          style={estilos.entrada}
        />
        <Picker
          style={estilos.entrada}
          selectedValue={usuario.nome}
          onValueChange={(itemValue, itemIndex) =>
            setUsuario((old) => ({ ...old, nome: itemValue }))
          }>
          <Picker.Item label="Renan" value="Renan" />
          <Picker.Item label="Samuel" value="Samuel" />
        </Picker>
        <View style={{ width: "80%", margin: "10%" }}>
          <DatePicker
            current={usuario.data}
            selected={usuario.data}
            mode="calendar"
            minuteInterval={30}
            onSelectedChange={(date) => setUsuario((old) => ({ data: date }))}
          />
        </View>
        <TouchableOpacity onPress={() => adicionar()} style={estilos.botao}>
          <Text style={estilos.textoBotao}>Adicionar</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}
