import React, { useState } from "react";
import { Text, View, TouchableOpacity, TextInput, Alert } from "react-native";
import estilos from "./estilos";
import {
  deletarRepositorioDoUsuario,
  salvarRepositoriosDoUsuario,
} from "../../service/reqs/repositorios";

export default function InfoRepositorio({ route, navigation }) {
  const [nome, setNome] = useState(route.params.item.name);
  const [data, setData] = useState(route.params.item.data);

  async function salvarRepo() {
    const resultado = await salvarRepositoriosDoUsuario(
      route.params.item.postId,
      nome,
      data,
      route.params.item.id
    );

    if (resultado === "sucesso") {
      Alert.alert("Repositório atualizado!");
      navigation.goBack();
    } else {
      Alert.alert("Erro ao autualizar repositório");
    }
  }

  async function deletarRepo() {
    const resultado = await deletarRepositorioDoUsuario(route.params.item.id);

    if (resultado === "sucesso") {
      Alert.alert("Repositório deletado!");
      navigation.goBack();
    } else {
      Alert.alert("Erro ao autualizar repositório");
    }
  }

  return (
    <View style={estilos.container}>
      <TextInput
        placeholder="Nome do repositório"
        autoCapitalize="none"
        style={estilos.entrada}
        value={nome}
        onChangeText={setNome}
      />
      <TextInput
        placeholder="Data de criação"
        autoCapitalize="none"
        style={estilos.entrada}
        value={data}
        onChangeText={setData}
      />
      <TouchableOpacity style={estilos.botao} onPress={() => salvarRepo()}>
        <Text style={estilos.textoBotao}>Salvar</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => deletarRepo()}
        style={[estilos.botao, { backgroundColor: "#DD2B2B", marginTop: 10 }]}
      >
        <Text style={estilos.textoBotao}>Deletar</Text>
      </TouchableOpacity>
    </View>
  );
}
