import React, { useEffect, useState } from "react";
import { Text, View, FlatList, TouchableOpacity } from "react-native";
import estilos from "./estilos";
import { pegarRepositoriosDoUsuario } from "../../service/reqs/repositorios";
import { useIsFocused } from "@react-navigation/native";
import { buscaUsuario, deletaUsuario } from "../../service/reqs/usuarios";
import moment from "moment/moment";
import { MaterialCommunityIcons } from '@expo/vector-icons';

export default function Repositorios({ route, navigation }) {
  const [repo, setRepo] = useState([]);
  console.log("üöÄ ~ file: index.js:10 ~ Repositorios ~ repo:", repo)
  const estaNaTela = useIsFocused();
  const carregarRepositorios = async () => {
    const resultado = await buscaUsuario();
    setRepo(resultado);
  };
  useEffect(() => {

    carregarRepositorios()
    carregarRepositorios();
  }, [estaNaTela]);

  return (
    <View style={estilos.container}>
      <Text style={estilos.repositoriosTexto}>
        {repo.length} gastos inclu√≠dos
      </Text>
      <TouchableOpacity
        style={estilos.botao}
        onPress={() =>
          navigation.navigate("Principal")
        }
      >
        <Text style={estilos.textoBotao}>Adicionar nova d√≠vida</Text>
      </TouchableOpacity>

      <FlatList
        data={repo}
        style={{ width: "90%" }}
        keyExtractor={(repo) => repo._id}
        renderItem={({ item }) => (
          <View style={{ display: "flex", justifyContent: "space-between", alignItems: "center", flexDirection: "row" }}>
            <View style={{
              marginBottom: 20,
              borderBottomColor: '#159',
              borderBottomWidth: StyleSheet.hairlineWidth,
            }}>
              <Text
                style={estilos.repositorioNome}

              >
                Data:
                <Text style={estilos.repositorioDataValor}>
                  {moment(item.data).format("DD/MM/YYYY")}
                </Text>
              </Text>
              <Text style={estilos.repositorioData}>
                Valor:
                <Text style={estilos.repositorioDataValor}>
                  {item.valor}
                </Text>
              </Text>
              <Text style={estilos.repositorioData}>
                Gasto: <Text style={estilos.repositorioDataValor}>
                  {item.local}
                </Text>
              </Text>
              <Text style={estilos.repositorioData}>
                Quem deve:
                <Text style={estilos.repositorioDataValor}>
                  {item.nome}
                </Text>
              </Text>
            </View>
            <TouchableOpacity
              onPress={() => deletaUsuario(item._id)}>
              <MaterialCommunityIcons name="trash-can-outline" size={24} color="black" />
            </TouchableOpacity>
          </View>
        )}
      />
    </View>
  );
}
