import React, { useEffect, useState } from "react";
import {
  Text,
  View,
  Image,
  TouchableOpacity,
  TextInput,
  Alert,
  ScrollView,
} from "react-native";
import estilos from "./estilos";
import { buscaUsuario, criaFinancas } from "../../service/reqs/usuarios";

export default function Principal({ navigation }) {
  const [usuario, setUsuario] = useState({ valor: 0, local: "", nome: "", data: "" });

  useEffect(() => {
    buscaUsuario();
  }, [])

  return (
    <ScrollView>
      <View style={estilos.container}>
        {usuario?.login && (
          <>
            <View style={estilos.fundo} />
            <View style={estilos.imagemArea}>
              <Image
                source={{
                  uri: usuario.avatar_url,
                }}
                style={estilos.imagem}
              />
            </View>
            <Text style={estilos.textoNome}>{usuario.login}</Text>
            <Text style={estilos.textoEmail}>{usuario.email}</Text>
            <View style={estilos.seguidoresArea}>
              <View style={estilos.seguidores}>
                <Text style={estilos.seguidoresNumero}>
                  {usuario.following}
                </Text>
                <Text style={estilos.seguidoresTexto}>Seguidores</Text>
              </View>
              <View style={estilos.seguidores}>
                <Text style={estilos.seguidoresNumero}>
                  {usuario.followers}
                </Text>
                <Text style={estilos.seguidoresTexto}>Seguindo</Text>
              </View>
            </View>
            <TouchableOpacity
              onPress={() =>
                navigation.navigate("Repositorios", { nome: usuario.login })
              }
            >
              <Text style={estilos.repositorios}>Ver os reposit√≥rios</Text>
            </TouchableOpacity>
          </>
        )}
        <TextInput
          placeholder="Valor"
          autoCapitalize="none"
          onChangeText={(e) => setUsuario({ valor: e.targ })}
          style={estilos.entrada}
        />
        <TextInput
          placeholder="Local"
          autoCapitalize="none"
          style={estilos.entrada}
        />
        <TextInput
          placeholder="Quem deve"
          autoCapitalize="none"
          style={estilos.entrada}
        />
        <TextInput
          placeholder="Data"
          autoCapitalize="none"
          style={estilos.entrada}
        />

        <TouchableOpacity onPress={criaFinancas(usuario)} style={estilos.botao}>
          <Text style={estilos.textoBotao}>Adicionar</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}
